const options = { timeZone: 'America/Sao_Paulo', hour12: false };
const brazilianTime = new Date(currentDate.toLocaleString('en-US', options));

// Obt√©m o dia da semana (0 = Domingo, 1 = Segunda, ..., 6 = S√°bado) e a hora
const dayOfWeek = brazilianTime.getDay(); // 0=Domingo, 1=Segunda, ..., 6=S√°bado
const hour = brazilianTime.getHours();

// Verifica se a hora est√° entre 08:00 e 18:00 e se o dia √© √∫til (segunda a sexta)
if (hour >= 9 && hour < 18 && dayOfWeek >= 1 && dayOfWeek <= 5) {
