  let data = pendencia.data || pendencia.data_ocorrencia || 'N/A';
  let contrato = pendencia.numero_contrato || 'N/A';
  let origem = pendencia.instituicao || pendencia.tipo || pendencia.tipo_financiamento || 'N/A';
  let valor = pendencia.valor || pendencia.valor_convenio || 'N/A';

  return `*Data:* _${data}_\n*Contrato:* _${contrato}_\n*Origem:* _${origem}_\n*Valor:* _${valor}_\n`;
});

// Junta todas as pendências formatadas em uma única string
