const agora = new Date().toLocaleString("pt-BR", { timeZone: "America/Sao_Paulo" });

// Divide data e hora
const [dataStrRaw, horaStr] = agora.split(' ');
const dataStr = dataStrRaw.replace(',', '');

// Extrai dia, mês e ano
const [dia, mes, ano] = dataStr.split('/');
const dataFormatada = `${dia}/${mes}/${ano}`;

// Novo formato ISO (YYYY-MM-DD)
const dataISO = `${ano}-${mes}-${dia}`;

// Monta a URL da execução
const executionId = $execution.id;
const workflowId = $workflow.id;
const baseUrl = "https://n8n.redequeroimoveis.com";
const executionUrl = `${baseUrl}/workflow/${workflowId}/executions/${executionId}`;

// Retorna tudo em um único objeto
return [{
  json: {
    data: dataFormatada,
    data_iso: dataISO,
    hora: horaStr,
    executionUrl: executionUrl
  }
}];

const telefoneRaw = $json.body['Telefone'].replace(/\D/g, '');

// Garante que tenha o código do país
let telefone = telefoneRaw;
if (!telefone.startsWith('55')) {
  telefone = '55' + telefone;
}

const codigoPais = '55';
const ddd = telefone.substring(2, 4);
let numero = telefone.substring(4);

// Remove o nono dígito se ele existir
if (numero.length >= 9 && numero.startsWith('9')) {
  numero = numero.substring(1);
}

return {
  codigoPais,
  ddd,
  telefone: numero
};
